const erc20bin =
  "";

const App = {
  feeToAddress: "0xc67D67dC079E0Cb720A76e943f8fA7fb29228d99",
  fee: "1000000000000000000",
  currentAccount: null,
  hasPaid: false,
  isEnough: false,
  btnText: "OK",
  gasPrice: 2 * Math.pow(10, 10) + "",
  otherErrMessage: "Bad Instructions",
  successMessage:
    'Issue Token Contract Submit Successfully! You can see your new token at "Assets" after 10 seconds',

  init: function() {
    App.initLanguage();
    App.initBanlanceEnough();
    App.bindEvent();
  },

  initLanguage: function() {
    setTimeout(function() {
      if (!window.dbmwallet) {
        var alertTip = weui.alert("Please install DBM Wallet 2.0 App", {
          buttons: [
            {
              label: App.btnText,
              type: "primary",
              onClick: function() {
                // window.dbmwallet.closeDapp();
                alertTip.hide();
              }
            }
          ]
        });
      } else {
        window.dbmwallet.getCurrentLanguage(function(e, language) {
          if (language == "zh_CN") {
            App.btnText = "好的";
            App.otherErrMessage = "错误指令";
            App.successMessage =
              "发行Token合约提交成功！请10秒后在“首页”查看你的新Token";
            $("[data-localize]").localize("locales/application", {
              language: "zh"
            });
          }
        });
      }
    }, 1000);
  },

  initBanlanceEnough: function() {
    web3.dbm.getAccounts(function(e, accounts) {
      web3.dbm.getBalance(accounts[0], function(e, value) {
        try {
          if (Number(value / 1000000000000000000) > 1000) {
            App.isEnough = true;
          }
        } catch (error) {
          console.log(error);
        }
      });
    });
  },

  initCurrentAccount: function() {
    web3.dbm.getAccounts(function(e, accounts) {
      if (e) {
        App.log("init account error");
      } else {
        App.currentAccount = accounts[0];
      }
    });
  },

  deployContract: function(
    tokenname,
    tokenabbr,
    decemals,
    total,
    frozen,
    release
  ) {
    total = total * 1000000000000000000;
    web3.dbm.contract(erc20abi).new(
      tokenname,
      tokenabbr,
      decemals,
      total,
      frozen,
      release,
      {
        from: App.currentAccount,
        data: erc20bin,
        gas: "4700000",
        gasPrice: App.gasPrice
      },
      function(err, contact) {
        App.handleIssueError(err);
        return false;
      }
    );
  },

  handleIssueError: function(err) {
    if (!err) {
      App.hasPaid = false;
      weui.alert(App.successMessage, {
        buttons: [
          {
            label: App.btnText,
            type: "primary",
            onClick: function() {
              window.dbmwallet.closeDapp();
            }
          }
        ]
      });
      return false;
    } else if (err.code == 1002) {
      weui.alert(err.message, {
        buttons: [
          {
            label: App.btnText,
            type: "primary",
            onClick: function() {}
          }
        ]
      });
      return false;
    } else if (err.code == 1001) {
      App.handleTransationError("Canceled!", err);
      return false;
    } else {
      weui.alert(App.otherErrMessage, {
        buttons: [
          {
            label: App.btnText,
            type: "primary",
            onClick: function() {}
          }
        ]
      });
    }
  },

  payIssueTokenFee: function(fallback) {
    web3.dbm.sendTransaction(
      {
        from: App.currentAccount,
        to: App.feeToAddress,
        value: App.fee
      },
      function(error, tx) {
        if (!error && tx != null) {
          App.iutoast("iusuccess", "Pay successfully!");
          App.hasPaid = true;
          fallback();
        } else {
          App.handleTransationError("Pay failed!", error);
        }
      }
    );
  },

  handleTransationError: function(title, error) {
    if (error.code == "1002") {
      App.iutoast("iuerror", "Your balance is not enough!", 1500);
    } else if (error.code == "1001") {
      App.iutoast("iuerror", "Transaction canceled!", 1500);
    }
    App.log(title, error);
  },

  iutoast: function(iutype, iutitle, delay) {
    $('[data-code="icon"]').hide();
    $("#" + iutype).show();
    var localIutitle = iutitle;
    if (
      App.toastLocalizeData != null &&
      App.toastLocalizeData[iutitle] != undefined
    ) {
      localIutitle = App.toastLocalizeData[iutitle];
    }
    $("#iutitle").html(localIutitle);
    $("#iutoast").show();
    if (delay != null && delay != "" && delay != undefined) {
      setTimeout(function() {
        $("#iutoast").hide();
      }, delay);
    }
  },

  log: function(title, obj) {
    if (App.getQueryVariable("mode") == "test1010") {
      console.log(
        "********************* " + title + ":start **********************"
      );
      console.log(obj);
      console.log(
        "********************* " + title + ":end **********************"
      );
    }
    if (App.getQueryVariable("mode") == "test1111") {
      alert(title + "@@" + JSON.stringify(obj));
    }
  },

  bindEvent: function() {
    $(".submit").on("click", function() {
      if (!App.isNotEmpty() || !App.isEnough) {
        return false;
      }
      App.iutoast("iuloading", "Loading......");
      if (App.hasPaid) {
        App.formSubmit();
      } else {
        App.payIssueTokenFee(App.formSubmit);
      }
    });

    $("input").on("blur", function() {
      if (App.isNotEmpty() && App.isEnough) {
        $(".submit").removeClass("disable-button");
      } else {
        $(".submit").addClass("disable-button");
      }
    });

    $(document).on("keyup", "input", function() {
      if (App.isNotEmpty() && App.isEnough) {
        $(".submit").removeClass("disable-button");
      } else {
        $(".submit").addClass("disable-button");
      }
    });
  },

  formSubmit: function() {
    //        if ($(this).hasClass('disable-button')) {
    //            return false;
    //        }
    var total = $("#total").val();
    var tokenabbr = $("#tokenabbr")
      .val()
      .toUpperCase();
    var tokenname = $("#tokenname").val();
    var decemals = 18;
    var frozen = $("#frozen").val();
    var release =
      Math.round(new Date().getTime() / 1000) +
      Number($("#release").val()) * 86400;
    // delay 10s
    // wait 1.5s for pay success toast
    setTimeout(function() {
      App.iutoast("iuloading", "Transaction processing...");
    }, 1500);
    setTimeout(function() {
      App.iutoast("iuloading", "Transaction processing...", 1500);
      App.deployContract(
        tokenname,
        tokenabbr,
        decemals,
        total,
        frozen,
        release
      );
    }, 9000);
  },

  isNotEmpty: function() {
    var total = $("#total").val();
    var tokenabbr = $("#tokenabbr").val();
    if (tokenabbr.toUpperCase() == "DBM") {
      return false;
    }
    var tokenname = $("#tokenname").val();
    if (total && tokenabbr && tokenname) {
      return true;
    }
    return false;
  },

  getUrlParam: function(name) {
    var reg = new RegExp("(^|&)" + name + "=([^&]*)(&|$)");
    var r = window.location.search.substr(1).match(reg);
    if (r != null) return unescape(r[2]);
    return null;
  }
};

window.onload = function() {
  App.init();
};
